import React from "react";
import HeroSection from "../components/home/HeroSection";
import StatsSection from "../components/home/StatsSection";
import ActionsList from "../components/actions/ActionsList";
import Footer from "../components/layout/Footer";

const Home = () => {
  const handleFindActions = () => {
    // Rolar para a seção de ações
    const actionsSection = document.getElementById("actions-section");
    if (actionsSection) {
      actionsSection.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <div className="bg-white">
      {/* Hero Section */}
      <HeroSection />

      {/* Stats Section */}
      <StatsSection />

      {/* Actions Section */}
      <section id="actions-section" className="section-modern">
        <div className="container-custom">
          <div className="text-center mb-5">
            <h2 className="section-title">Ações Disponíveis</h2>
            <p className="section-subtitle">
              Encontre oportunidades de voluntariado que se encaixam no seu perfil e disponibilidade
            </p>
          </div>

          <ActionsList />
        </div>
      </section>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default Home;
