// Serviço mockado para simular a API quando o backend não está disponível

// Simular delay de rede
const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

// Serviços de autenticação mockados
export const authServiceMock = {
  // Login de voluntário
  loginVolunteer: async (credentials) => {
    console.log("Mock - Login de voluntário:", credentials);
    await delay(1000); // Simular delay de rede

    if (!credentials.email || !credentials.password) {
      throw new Error("Email e senha são obrigatórios");
    }

    return {
      message: "Login realizado com sucesso!",
      token: "mock_token_" + Date.now(),
      user: {
        id: "mock_user_id",
        name: "Usuário Mock",
        email: credentials.email,
        type: "volunteer",
      },
    };
  },

  // Login de ONG
  loginONG: async (credentials) => {
    console.log("Mock - Login de ONG:", credentials);
    await delay(1000); // Simular delay de rede

    if (!credentials.email || !credentials.password) {
      throw new Error("Email e senha são obrigatórios");
    }

    return {
      message: "Login realizado com sucesso!",
      token: "mock_token_" + Date.now(),
      ong: {
        id: "mock_ong_id",
        name: "ONG Mock",
        email: credentials.email,
        type: "ong",
      },
    };
  },

  // Cadastro de voluntário
  registerVolunteer: async (userData) => {
    console.log("Mock - Cadastro de voluntário:", userData);
    await delay(1500); // Simular delay de rede

    const { name, email, phone, city, state, password } = userData;

    if (!name || !email || !password || !city || !state) {
      throw new Error("Campos obrigatórios não preenchidos");
    }

    return {
      message: "Voluntário cadastrado com sucesso!",
      token: "mock_token_" + Date.now(),
      user: {
        id: "mock_user_id_" + Date.now(),
        name,
        email,
        phone,
        city,
        state,
        type: "volunteer",
      },
    };
  },

  // Cadastro de ONG
  registerONG: async (ongData) => {
    console.log("Mock - Cadastro de ONG:", ongData);
    await delay(1500); // Simular delay de rede

    const { name, email, cnpj, area, city, state, password } = ongData;

    if (!name || !email || !cnpj || !area || !city || !state || !password) {
      throw new Error("Campos obrigatórios não preenchidos");
    }

    return {
      message: "ONG cadastrada com sucesso!",
      token: "mock_token_" + Date.now(),
      ong: {
        id: "mock_ong_id_" + Date.now(),
        name,
        email,
        cnpj,
        area,
        city,
        state,
        type: "ong",
      },
    };
  },

  // Verificar usuário atual
  getCurrentUser: async () => {
    await delay(500);
    return {
      user: {
        id: "mock_user_id",
        name: "Usuário Mock",
        email: "mock@example.com",
        type: "volunteer",
      },
    };
  },

  // Atualizar perfil
  updateProfile: async (userData) => {
    await delay(1000);
    return {
      message: "Perfil atualizado com sucesso!",
      user: userData,
    };
  },
};

// Utilitários mockados
export const apiUtilsMock = {
  // Tratar erros da API
  handleError: (error) => {
    if (error.message) {
      return error.message;
    }
    return "Erro desconhecido";
  },

  // Verificar se é erro de validação
  isValidationError: (error) => {
    return error.message && error.message.includes("obrigatório");
  },

  // Verificar se é erro de autenticação
  isAuthError: (error) => {
    return error.message && error.message.includes("credenciais");
  },

  // Verificar se é erro de autorização
  isForbiddenError: (error) => {
    return false;
  },
};

export default authServiceMock;
