import React, { useState } from "react";
import {
  Users,
  CheckCircle,
  XCircle,
  Clock,
  MessageCircle,
  Phone,
  Mail,
} from "lucide-react";

const Applications = () => {
  const [activeTab, setActiveTab] = useState("pending");

  // Dados mockados para demonstração
  const applications = {
    pending: [
      {
        id: 1,
        volunteerName: "Maria Silva",
        actionTitle: "Apoio Educacional para Crianças",
        applicationDate: "2024-01-15",
        status: "pending",
        statusText: "Aguardando contato",
        phone: "(11) 99999-9999",
        email: "maria.silva@email.com",
        city: "São Paulo",
        state: "SP",
        skills: ["Educação", "Pedagogia"],
        availability: "Manhãs e tardes",
        notes:
          "Tenho experiência com crianças e gostaria de ajudar na área educacional.",
      },
      {
        id: 2,
        volunteerName: "João Santos",
        actionTitle: "Limpeza de Praias",
        applicationDate: "2024-01-14",
        status: "pending",
        statusText: "Aguardando contato",
        phone: "(11) 88888-8888",
        email: "joao.santos@email.com",
        city: "São Paulo",
        state: "SP",
        skills: ["Meio Ambiente", "Organização"],
        availability: "Fins de semana",
        notes:
          "Sou apaixonado por meio ambiente e quero contribuir para a preservação das praias.",
      },
    ],
    contacted: [
      {
        id: 3,
        volunteerName: "Ana Costa",
        actionTitle: "Apoio Educacional para Crianças",
        applicationDate: "2024-01-10",
        status: "contacted",
        statusText: "Contato realizado",
        phone: "(11) 77777-7777",
        email: "ana.costa@email.com",
        city: "São Paulo",
        state: "SP",
        skills: ["Educação", "Psicologia"],
        availability: "Tardes",
        notes: "Psicóloga com experiência em educação infantil.",
        contactAttempts: [
          {
            date: "2024-01-12",
            method: "Telefone",
            success: true,
            notes: "Voluntária confirmou interesse",
          },
        ],
      },
    ],
    accepted: [
      {
        id: 4,
        volunteerName: "Carlos Mendes",
        actionTitle: "Limpeza de Praias",
        applicationDate: "2024-01-08",
        status: "accepted",
        statusText: "Aceita",
        phone: "(11) 66666-6666",
        email: "carlos.mendes@email.com",
        city: "São Paulo",
        state: "SP",
        skills: ["Meio Ambiente", "Liderança"],
        availability: "Fins de semana",
        notes:
          "Líder de grupo de escoteiros, experiência em atividades ao ar livre.",
        contactAttempts: [
          {
            date: "2024-01-10",
            method: "Telefone",
            success: true,
            notes: "Voluntário aceitou participar",
          },
        ],
        startDate: "2024-01-25",
        hours: 6,
      },
    ],
    rejected: [
      {
        id: 5,
        volunteerName: "Patrícia Lima",
        actionTitle: "Apoio Educacional para Crianças",
        applicationDate: "2024-01-05",
        status: "rejected",
        statusText: "Rejeitada",
        phone: "(11) 55555-5555",
        email: "patricia.lima@email.com",
        city: "São Paulo",
        state: "SP",
        skills: ["Administração"],
        availability: "Noites",
        notes: "Sem experiência com crianças.",
        contactAttempts: [
          {
            date: "2024-01-07",
            method: "Telefone",
            success: true,
            notes: "Voluntária não tinha perfil adequado",
          },
        ],
        rejectionReason: "Perfil não adequado para a ação",
      },
    ],
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "pending":
        return "bg-warning-100 text-warning-700";
      case "contacted":
        return "bg-primary-100 text-primary-700";
      case "accepted":
        return "bg-success-100 text-success-700";
      case "rejected":
        return "bg-error-100 text-error-700";
      default:
        return "bg-gray-100 text-gray-700";
    }
  };

  const tabs = [
    {
      id: "pending",
      name: "Aguardando Contato",
      count: applications.pending.length,
    },
    {
      id: "contacted",
      name: "Contatados",
      count: applications.contacted.length,
    },
    { id: "accepted", name: "Aceitas", count: applications.accepted.length },
    { id: "rejected", name: "Rejeitadas", count: applications.rejected.length },
  ];

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("pt-BR");
  };

  const handleStatusChange = (applicationId, newStatus) => {
    console.log(
      `Mudando status da candidatura ${applicationId} para ${newStatus}`
    );
  };

  const handleContactAttempt = (applicationId) => {
    console.log(
      `Registrando tentativa de contato para candidatura ${applicationId}`
    );
  };

  return (
    <div className="p-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-heading font-bold text-gray-900 mb-2">
          Candidaturas Recebidas
        </h1>
        <p className="text-gray-600">
          Gerencie todas as candidaturas dos voluntários para suas ações
        </p>
      </div>

      {/* Estatísticas */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-white rounded-2xl shadow-soft p-6 text-center">
          <div className="w-12 h-12 bg-warning-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <Clock className="w-6 h-6 text-warning-600" />
          </div>
          <div className="text-2xl font-bold text-warning-600">
            {applications.pending.length}
          </div>
          <div className="text-sm text-gray-600">Aguardando Contato</div>
        </div>

        <div className="bg-white rounded-2xl shadow-soft p-6 text-center">
          <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <MessageCircle className="w-6 h-6 text-primary-600" />
          </div>
          <div className="text-2xl font-bold text-primary-600">
            {applications.contacted.length}
          </div>
          <div className="text-sm text-gray-600">Contatados</div>
        </div>

        <div className="bg-white rounded-2xl shadow-soft p-6 text-center">
          <div className="w-12 h-12 bg-success-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <CheckCircle className="w-6 h-6 text-success-600" />
          </div>
          <div className="text-2xl font-bold text-success-600">
            {applications.accepted.length}
          </div>
          <div className="text-sm text-gray-600">Aceitas</div>
        </div>

        <div className="bg-white rounded-2xl shadow-soft p-6 text-center">
          <div className="w-12 h-12 bg-error-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <XCircle className="w-6 h-6 text-error-600" />
          </div>
          <div className="text-2xl font-bold text-error-600">
            {applications.rejected.length}
          </div>
          <div className="text-sm text-gray-600">Rejeitadas</div>
        </div>
      </div>

      {/* Tabs */}
      <div className="border-b border-gray-200 mb-8">
        <nav className="-mb-px flex space-x-8">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`py-4 px-1 border-b-2 font-medium text-sm transition-colors ${
                activeTab === tab.id
                  ? "border-secondary-500 text-secondary-600"
                  : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
              }`}
            >
              {tab.name}
              <span
                className={`ml-2 py-0.5 px-2.5 rounded-full text-xs font-medium ${
                  activeTab === tab.id
                    ? "bg-secondary-100 text-secondary-600"
                    : "bg-gray-100 text-gray-600"
                }`}
              >
                {tab.count}
              </span>
            </button>
          ))}
        </nav>
      </div>

      {/* Content */}
      <div className="space-y-6">
        {applications[activeTab].length === 0 ? (
          <div className="text-center py-12">
            <Users className="w-16 h-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-gray-600 mb-2">
              Nenhuma candidatura encontrada
            </h3>
            <p className="text-gray-500">
              {activeTab === "pending" &&
                "Nenhuma candidatura aguardando contato."}
              {activeTab === "contacted" &&
                "Nenhuma candidatura contatada ainda."}
              {activeTab === "accepted" && "Nenhuma candidatura aceita ainda."}
              {activeTab === "rejected" &&
                "Nenhuma candidatura rejeitada ainda."}
            </p>
          </div>
        ) : (
          applications[activeTab].map((application) => (
            <div
              key={application.id}
              className="bg-white rounded-2xl shadow-soft p-6"
            >
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  {/* Header */}
                  <div className="flex items-center space-x-3 mb-4">
                    <span
                      className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(
                        application.status
                      )}`}
                    >
                      {application.statusText}
                    </span>
                    <span className="text-sm text-gray-500">
                      Candidatura em {formatDate(application.applicationDate)}
                    </span>
                  </div>

                  {/* Informações do Voluntário */}
                  <h3 className="text-xl font-heading font-semibold text-gray-900 mb-2">
                    {application.volunteerName}
                  </h3>
                  <p className="text-gray-600 mb-4">
                    <strong>Ação:</strong> {application.actionTitle}
                  </p>

                  {/* Contato */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                    <div className="flex items-center space-x-2 text-sm text-gray-600">
                      <Phone className="w-4 h-4" />
                      <span>{application.phone}</span>
                    </div>
                    <div className="flex items-center space-x-2 text-sm text-gray-600">
                      <Mail className="w-4 h-4" />
                      <span>{application.email}</span>
                    </div>
                    <div className="text-sm text-gray-600">
                      <strong>Local:</strong> {application.city},{" "}
                      {application.state}
                    </div>
                    <div className="text-sm text-gray-600">
                      <strong>Disponibilidade:</strong>{" "}
                      {application.availability}
                    </div>
                  </div>

                  {/* Habilidades */}
                  <div className="mb-4">
                    <span className="text-sm text-gray-600 font-medium">
                      Habilidades:
                    </span>
                    <div className="flex flex-wrap gap-2 mt-2">
                      {application.skills.map((skill, index) => (
                        <span
                          key={index}
                          className="bg-secondary-100 text-secondary-700 px-2 py-1 rounded-full text-xs"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>

                  {/* Observações */}
                  {application.notes && (
                    <div className="bg-gray-50 rounded-xl p-4 mb-4">
                      <span className="text-sm text-gray-600 font-medium">
                        Observações:
                      </span>
                      <p className="text-gray-700 mt-1">{application.notes}</p>
                    </div>
                  )}

                  {/* Tentativas de Contato */}
                  {application.contactAttempts &&
                    application.contactAttempts.length > 0 && (
                      <div className="bg-blue-50 rounded-xl p-4 mb-4">
                        <span className="text-sm text-blue-600 font-medium">
                          Tentativas de Contato:
                        </span>
                        <div className="mt-2 space-y-2">
                          {application.contactAttempts.map((attempt, index) => (
                            <div key={index} className="text-sm text-blue-700">
                              <strong>{formatDate(attempt.date)}</strong> -{" "}
                              {attempt.method} - {attempt.notes}
                            </div>
                          ))}
                        </div>
                      </div>
                    )}

                  {/* Motivo da Rejeição */}
                  {application.rejectionReason && (
                    <div className="bg-red-50 rounded-xl p-4 mb-4">
                      <span className="text-sm text-red-600 font-medium">
                        Motivo da Rejeição:
                      </span>
                      <p className="text-red-700 mt-1">
                        {application.rejectionReason}
                      </p>
                    </div>
                  )}

                  {/* Informações de Participação */}
                  {application.startDate && (
                    <div className="bg-green-50 rounded-xl p-4 mb-4">
                      <span className="text-sm text-green-600 font-medium">
                        Participação Confirmada:
                      </span>
                      <div className="mt-2 grid grid-cols-2 gap-4 text-sm text-green-700">
                        <div>
                          <strong>Data de Início:</strong>{" "}
                          {formatDate(application.startDate)}
                        </div>
                        <div>
                          <strong>Horas:</strong> {application.hours}h
                        </div>
                      </div>
                    </div>
                  )}
                </div>

                {/* Ações */}
                <div className="ml-6 flex flex-col space-y-2">
                  {application.status === "pending" && (
                    <>
                      <button
                        onClick={() =>
                          handleStatusChange(application.id, "contacted")
                        }
                        className="btn-primary text-sm px-4 py-2"
                      >
                        Marcar como Contatado
                      </button>
                      <button
                        onClick={() => handleContactAttempt(application.id)}
                        className="btn-secondary text-sm px-4 py-2"
                      >
                        Registrar Contato
                      </button>
                    </>
                  )}

                  {application.status === "contacted" && (
                    <>
                      <button
                        onClick={() =>
                          handleStatusChange(application.id, "accepted")
                        }
                        className="btn-success text-sm px-4 py-2"
                      >
                        Aceitar
                      </button>
                      <button
                        onClick={() =>
                          handleStatusChange(application.id, "rejected")
                        }
                        className="btn-error text-sm px-4 py-2"
                      >
                        Rejeitar
                      </button>
                    </>
                  )}

                  <button className="btn-secondary text-sm px-4 py-2">
                    Ver Perfil Completo
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Applications;
