import React, { useEffect, useMemo, useState } from "react";
import { Link } from "react-router-dom";
import { useAuthStore } from "../../stores/authStore";
import { Calendar, Users, Building2 } from "lucide-react";
import CreateActionModal from "../../components/actions/CreateActionModal";
import { ongsService, applicationsService } from "../../services/api";

const ONGDashboard = () => {
  const { user } = useAuthStore();
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [statsData, setStatsData] = useState(null);
  const [myActions, setMyActions] = useState([]);
  const [receivedApplications, setReceivedApplications] = useState([]);

  useEffect(() => {
    let isMounted = true;
    const load = async () => {
      setLoading(true);
      setError("");
      try {
        const [statsRes, actionsRes, appsRes] = await Promise.all([
          ongsService.getStats(),
          ongsService.getMyActions({ limit: 6 }),
          applicationsService.getByONG({ limit: 6 }),
        ]);
        if (!isMounted) return;
        setStatsData(statsRes?.stats || null);
        setMyActions(actionsRes?.actions || []);
        setReceivedApplications(appsRes?.applications || []);
      } catch (err) {
        console.error("Erro ao carregar dashboard da ONG:", err);
        if (!isMounted) return;
        setError("Não foi possível carregar os dados agora.");
      } finally {
        if (isMounted) setLoading(false);
      }
    };
    load();
    return () => {
      isMounted = false;
    };
  }, []);

  const stats = useMemo(() => {
    const s = statsData || {};
    return {
      totalActions: s.totalActions || 0,
      activeActions: s.activeActions || 0,
      totalVolunteers: s.totalVolunteers || 0,
      pendingApplications: s.pendingApplications || 0,
    };
  }, [statsData]);

  const recentActions = useMemo(() => {
    return (myActions || []).slice(0, 6).map((a) => ({
      id: String(a._id || Math.random()),
      title: a.title || "Ação",
      date: a.date ? new Date(a.date).toLocaleDateString("pt-BR") : "-",
      volunteers: Math.max(0, a.currentVolunteers || 0),
      status: a.status || "active",
      area: a.area || "",
    }));
  }, [myActions]);

  const recentApplications = useMemo(() => {
    return (receivedApplications || []).slice(0, 6).map((app) => ({
      id: String(app._id || Math.random()),
      volunteerName: app.volunteer?.name || "Voluntário",
      actionTitle: app.action?.title || "Ação",
      appliedAt: app.createdAt
        ? new Date(app.createdAt).toLocaleDateString("pt-BR")
        : "-",
      status: app.status || "pending",
    }));
  }, [receivedApplications]);

  const getStatusColor = (status) => {
    switch (status) {
      case "active":
        return "text-green-600 bg-green-100 border-green-200";
      case "completed":
        return "text-blue-600 bg-blue-100 border-blue-200";
      case "pending":
        return "text-yellow-600 bg-yellow-100 border-yellow-200";
      case "approved":
        return "text-green-600 bg-green-100 border-green-200";
      default:
        return "text-gray-600 bg-gray-100 border-gray-200";
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case "active":
        return "Ativa";
      case "completed":
        return "Concluída";
      case "pending":
        return "Pendente";
      case "approved":
        return "Aprovada";
      default:
        return status;
    }
  };

  if (loading) {
    return (
      <div className="p-8 min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Carregando seu painel...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8 min-h-screen flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-600">{error}</p>
          <button
            className="mt-4 bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700"
            onClick={() => {
              setLoading(true);
              setError("");
              Promise.resolve().then(() => setLoading(false));
            }}
          >
            Tentar novamente
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <section className="bg-indigo-600 text-white py-8 mb-8">
        <div className="max-w-7xl mx-auto px-4">
          <div className="flex flex-col lg:flex-row items-start lg:items-center justify-between gap-6">
            <div>
              <h1 className="text-3xl font-bold mb-2">
                Bem-vindo, {user?.name || "ONG"}
              </h1>
              <p className="text-indigo-100">
                Gerencie suas ações sociais e voluntários
              </p>
            </div>

            <div className="flex gap-3">
              <button
                onClick={() => setIsCreateModalOpen(true)}
                className="px-6 py-3 bg-yellow-400 text-gray-900 rounded-lg hover:bg-yellow-300 font-medium"
              >
                Criar Ação
              </button>

              <Link
                to="/ong/profile"
                className="px-6 py-3 border border-white/30 text-white rounded-lg hover:bg-white/10 font-medium"
              >
                Perfil
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Stats Cards */}
      <section className="mb-8">
        <div className="max-w-7xl mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {/* Total Actions */}
            <div className="bg-white p-6 rounded-lg shadow border">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 bg-blue-500 rounded-lg flex items-center justify-center">
                  <Building2 className="w-6 h-6 text-white" />
                </div>
                <div>
                  <div className="text-2xl font-bold text-blue-600">
                    {stats.totalActions}
                  </div>
                  <div className="text-gray-600 text-sm">Total de Ações</div>
                </div>
              </div>
            </div>

            {/* Active Actions */}
            <div className="bg-white p-6 rounded-lg shadow border">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 bg-green-500 rounded-lg flex items-center justify-center">
                  <Calendar className="w-6 h-6 text-white" />
                </div>
                <div>
                  <div className="text-2xl font-bold text-green-600">
                    {stats.activeActions}
                  </div>
                  <div className="text-gray-600 text-sm">Ações Ativas</div>
                </div>
              </div>
            </div>

            {/* Total Volunteers */}
            <div className="bg-white p-6 rounded-lg shadow border">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 bg-purple-500 rounded-lg flex items-center justify-center">
                  <Users className="w-6 h-6 text-white" />
                </div>
                <div>
                  <div className="text-2xl font-bold text-purple-600">
                    {stats.totalVolunteers}
                  </div>
                  <div className="text-gray-600 text-sm">Voluntários</div>
                </div>
              </div>
            </div>

            {/* Pending Applications */}
            <div className="bg-white p-6 rounded-lg shadow border">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 bg-orange-500 rounded-lg flex items-center justify-center">
                  <Users className="w-6 h-6 text-white" />
                </div>
                <div>
                  <div className="text-2xl font-bold text-orange-600">
                    {stats.pendingApplications}
                  </div>
                  <div className="text-gray-600 text-sm">
                    Candidaturas Pendentes
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Recent Activity */}
      <section className="mb-8">
        <div className="max-w-7xl mx-auto px-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Recent Actions */}
            <div className="bg-white rounded-lg shadow border p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-bold text-gray-900">
                  Ações Recentes
                </h3>
                <Link
                  to="/ong/actions"
                  className="text-indigo-600 hover:text-indigo-700 text-sm"
                >
                  Ver todas
                </Link>
              </div>

              <div className="space-y-3">
                {recentActions.length === 0 ? (
                  <p className="text-sm text-gray-500">Sem ações recentes.</p>
                ) : (
                  recentActions.slice(0, 3).map((action) => (
                    <div
                      key={action.id}
                      className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg"
                    >
                      <div className="w-8 h-8 bg-indigo-500 rounded-lg flex items-center justify-center">
                        <Calendar className="w-4 h-4 text-white" />
                      </div>
                      <div className="flex-1">
                        <h4 className="font-medium text-gray-900 text-sm">
                          {action.title}
                        </h4>
                        <div className="text-xs text-gray-600">
                          {action.date} • {action.volunteers} voluntários
                        </div>
                      </div>
                      <span
                        className={`px-2 py-1 rounded text-xs font-medium ${getStatusColor(
                          action.status
                        )}`}
                      >
                        {getStatusText(action.status)}
                      </span>
                    </div>
                  ))
                )}
              </div>
            </div>

            {/* Recent Applications */}
            <div className="bg-white rounded-lg shadow border p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-bold text-gray-900">
                  Candidaturas Recentes
                </h3>
                <Link
                  to="/ong/applications"
                  className="text-indigo-600 hover:text-indigo-700 text-sm"
                >
                  Ver todas
                </Link>
              </div>

              <div className="space-y-3">
                {recentApplications.length === 0 ? (
                  <p className="text-sm text-gray-500">
                    Sem candidaturas recentes.
                  </p>
                ) : (
                  recentApplications.slice(0, 3).map((application) => (
                    <div
                      key={application.id}
                      className="flex items-center gap-3 p-3 bg-gray-50 rounded-lg"
                    >
                      <div className="w-8 h-8 bg-green-500 rounded-lg flex items-center justify-center">
                        <Users className="w-4 h-4 text-white" />
                      </div>
                      <div className="flex-1">
                        <h4 className="font-medium text-gray-900 text-sm">
                          {application.volunteerName}
                        </h4>
                        <div className="text-xs text-gray-600">
                          {application.actionTitle}
                        </div>
                      </div>
                      <span
                        className={`px-2 py-1 rounded text-xs font-medium ${getStatusColor(
                          application.status
                        )}`}
                      >
                        {getStatusText(application.status)}
                      </span>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Quick Actions */}
      <section className="mb-8">
        <div className="max-w-7xl mx-auto px-4">
          <h2 className="text-xl font-bold text-gray-900 mb-6">
            Ações Rápidas
          </h2>

          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {[
              {
                title: "Criar Ação",
                action: () => setIsCreateModalOpen(true),
              },
              {
                title: "Gerenciar Ações",
                link: "/ong/actions",
              },
              {
                title: "Candidaturas",
                link: "/ong/applications",
              },
              {
                title: "Editar Perfil",
                link: "/ong/profile",
              },
            ].map((item, index) => (
              <div key={index}>
                {item.link ? (
                  <Link
                    to={item.link}
                    className="block bg-white rounded-lg shadow border p-4 text-center hover:bg-gray-50"
                  >
                    <h3 className="text-sm font-medium text-gray-900">
                      {item.title}
                    </h3>
                  </Link>
                ) : (
                  <button
                    onClick={item.action}
                    className="w-full bg-white rounded-lg shadow border p-4 text-center hover:bg-gray-50"
                  >
                    <h3 className="text-sm font-medium text-gray-900">
                      {item.title}
                    </h3>
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Create Action Modal */}
      <CreateActionModal
        isOpen={isCreateModalOpen}
        onClose={() => setIsCreateModalOpen(false)}
        onSuccess={() => {
          setIsCreateModalOpen(false);
          // Atualizar dados após criar ação
          // Poderíamos re-buscar as listas aqui se desejado
        }}
      />
    </div>
  );
};

export default ONGDashboard;
