import React, { useState, useEffect, useCallback } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import { useAuthStore } from "../stores/authStore";
import { actionsService } from "../services/api";
import {
  Calendar,
  MapPin,
  Users,
  Building2,
  Heart,
  Share2,
  ArrowLeft,
  Star,
  Phone,
  Mail,
  Globe,
  Clock as ClockIcon,
  Award,
  Eye,
  MessageCircle,
  Info,
  X,
} from "lucide-react";

const ActionDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { isAuthenticated } = useAuthStore();

  const [action, setAction] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [isApplying, setIsApplying] = useState(false);
  const [showFullDescription, setShowFullDescription] = useState(false);

  const loadActionDetails = useCallback(async () => {
    try {
      setLoading(true);
      setError("");

      // Buscar ação específica do banco de dados
      const actionData = await actionsService.getById(id);
      setAction(actionData);
    } catch (err) {
      console.error("Erro ao carregar ação:", err);
      setError("Erro ao carregar detalhes da ação");
    } finally {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    loadActionDetails();
  }, [id, loadActionDetails]);

  const handleApply = async () => {
    if (!isAuthenticated) {
      // Abrir modal de login
      window.dispatchEvent(new CustomEvent("openLoginModal"));
      return;
    }

    try {
      setIsApplying(true);
      // Aqui você implementaria a lógica de candidatura
      await new Promise((resolve) => setTimeout(resolve, 2000));

      // Simular sucesso
      alert(
        "Candidatura enviada com sucesso! A ONG entrará em contato em breve."
      );
    } catch (error) {
      alert("Erro ao enviar candidatura. Tente novamente.");
    } finally {
      setIsApplying(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString("pt-BR", {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const formatTime = (timeString) => {
    return timeString;
  };

  const getVolunteerStatus = () => {
    const percentage =
      (action?.currentVolunteers / action?.maxVolunteers) * 100;
    if (percentage >= 90)
      return {
        text: "Quase Lotado",
        color: "text-orange-600",
        bgColor: "bg-orange-100",
      };
    if (percentage >= 70)
      return {
        text: "Lotação Média",
        color: "text-yellow-600",
        bgColor: "bg-yellow-100",
      };
    return {
      text: "Vagas Disponíveis",
      color: "text-green-600",
      bgColor: "bg-green-100",
    };
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="relative inline-block">
            <div className="w-20 h-20 border-4 border-blue-200 rounded-full animate-spin"></div>
            <div className="absolute top-0 left-0 w-20 h-20 border-4 border-transparent border-t-blue-600 rounded-full animate-spin"></div>
          </div>
          <h3 className="text-xl font-semibold text-gray-700 mt-6 mb-2">
            Carregando ação...
          </h3>
          <p className="text-gray-500">Buscando detalhes da ação social</p>
        </div>
      </div>
    );
  }

  if (error || !action) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="w-20 h-20 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <X className="w-10 h-10 text-red-600" />
          </div>
          <h3 className="text-xl font-semibold text-gray-700 mb-2">
            Erro ao carregar ação
          </h3>
          <p className="text-gray-500 mb-4">{error || "Ação não encontrada"}</p>
          <button
            onClick={() => navigate(-1)}
            className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition-colors"
          >
            Voltar
          </button>
        </div>
      </div>
    );
  }

  const volunteerStatus = getVolunteerStatus();

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header com navegação */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <button
              onClick={() => navigate(-1)}
              className="flex items-center gap-2 text-gray-600 hover:text-gray-900 transition-colors"
            >
              <ArrowLeft className="w-5 h-5" />
              Voltar
            </button>

            <div className="flex items-center gap-4">
              <button className="p-2 text-gray-400 hover:text-gray-600 transition-colors">
                <Share2 className="w-5 h-5" />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600 transition-colors">
                <Heart className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Hero Section com imagem principal */}
      <section className="relative bg-gradient-to-br from-blue-600 via-blue-700 to-blue-800 text-white py-12 overflow-hidden">
        {/* Background Pattern */}
        <div className="absolute inset-0 opacity-10">
          <div
            className="absolute inset-0"
            style={{
              backgroundImage: `url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%23ffffff' fill-opacity='0.1'%3E%3Ccircle cx='30' cy='30' r='2'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")`,
            }}
          ></div>
        </div>

        {/* Floating Elements */}
        <div className="absolute top-20 left-10 w-20 h-20 bg-white opacity-10 rounded-full animate-pulse"></div>
        <div
          className="absolute top-40 right-20 w-16 h-16 bg-white opacity-10 rounded-full animate-pulse"
          style={{ animationDelay: "1s" }}
        ></div>

        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <div>
              {/* Badge de área */}
              <div className="inline-flex items-center gap-2 bg-white/10 backdrop-blur-sm border border-white/20 rounded-full px-4 py-2 mb-6">
                <span className="w-2 h-2 bg-yellow-400 rounded-full animate-pulse"></span>
                <span className="text-sm font-medium">{action.area}</span>
              </div>

              <h1 className="text-4xl md:text-5xl font-bold mb-6 leading-tight">
                {action.title}
              </h1>

              <p className="text-xl text-blue-100 mb-8 leading-relaxed">
                {action.description ? action.description.split("\n")[0] : "Descrição não disponível"}
              </p>

              {/* Stats rápidos */}
              <div className="grid grid-cols-3 gap-6">
                <div className="text-center">
                  <div className="text-2xl font-bold text-yellow-300">
                    {action.maxVolunteers || 0}
                  </div>
                  <div className="text-sm text-blue-200">Vagas</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-yellow-300">
                    {action.duration || 0}h
                  </div>
                  <div className="text-sm text-blue-200">Duração</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-yellow-300">
                    {action.currentVolunteers || 0}
                  </div>
                  <div className="text-sm text-blue-200">Inscritos</div>
                </div>
              </div>
            </div>

            {/* Imagem principal */}
            <div className="relative">
              {action.images && action.images[0] ? (
                <div className="relative rounded-2xl overflow-hidden shadow-2xl">
                  <img
                    src={action.images[0].url}
                    alt={action.images[0].caption || "Imagem da ação"}
                    className="w-full h-80 object-cover"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent"></div>
                  <div className="absolute bottom-4 left-4 text-white">
                    <p className="text-sm opacity-90">
                      {action.images[0].caption || "Imagem da ação"}
                    </p>
                  </div>
                </div>
              ) : (
                <div className="relative rounded-2xl overflow-hidden shadow-2xl bg-gradient-to-br from-blue-100 to-purple-100 h-80 flex items-center justify-center">
                  <div className="text-center text-gray-500">
                    <Building2 className="w-16 h-16 mx-auto mb-4 opacity-50" />
                    <p className="text-lg font-medium">Imagem não disponível</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </section>

      {/* Conteúdo principal */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-12">
          {/* Coluna principal - Conteúdo */}
          <div className="lg:col-span-2 space-y-12">
            {/* Descrição completa */}
            <section className="bg-white rounded-2xl shadow-lg border border-gray-100 p-8">
              <h2 className="text-2xl font-bold text-gray-900 mb-6 flex items-center gap-3">
                <MessageCircle className="w-6 h-6 text-blue-600" />
                Sobre a Ação
              </h2>

              <div className="prose prose-lg max-w-none">
                {action.description ? (
                  <>
                    {showFullDescription ? (
                      <div className="whitespace-pre-line text-gray-700 leading-relaxed">
                        {action.description}
                      </div>
                    ) : (
                      <div className="text-gray-700 leading-relaxed">
                        {action.description.split("\n").slice(0, 3).join("\n")}
                        {action.description.split("\n").length > 3 && (
                          <span>...</span>
                        )}
                      </div>
                    )}

                    {action.description.split("\n").length > 3 && (
                      <button
                        onClick={() => setShowFullDescription(!showFullDescription)}
                        className="mt-4 text-blue-600 hover:text-blue-700 font-medium flex items-center gap-2"
                      >
                        {showFullDescription ? (
                          <>
                            <Eye className="w-4 h-4" />
                            Ver menos
                          </>
                        ) : (
                          <>
                            <Eye className="w-4 h-4" />
                            Ver descrição completa
                          </>
                        )}
                      </button>
                    )}
                  </>
                ) : (
                  <p className="text-gray-500 italic">Descrição não disponível</p>
                )}
              </div>
            </section>

            {/* Requisitos e habilidades */}
            <section className="bg-white rounded-2xl shadow-lg border border-gray-100 p-8">
              <h2 className="text-2xl font-bold text-gray-900 mb-6 flex items-center gap-3">
                <Award className="w-6 h-6 text-green-600" />
                Requisitos e Habilidades
              </h2>

              <div className="space-y-6">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-3">
                    Requisitos
                  </h3>
                  <p className="text-gray-700 leading-relaxed">
                    {action.requirements || "Requisitos não especificados"}
                  </p>
                </div>

                {action.skills && action.skills.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900 mb-3">
                      Habilidades Desejadas
                    </h3>
                    <div className="flex flex-wrap gap-2">
                      {action.skills.map((skill, index) => (
                        <span
                          key={index}
                          className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm font-medium"
                        >
                          {skill}
                        </span>
                      ))}
                    </div>
                  </div>
                )}

                {action.tags && action.tags.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900 mb-3">
                      Tags
                    </h3>
                    <div className="flex flex-wrap gap-2">
                      {action.tags.map((tag, index) => (
                        <span
                          key={index}
                          className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm font-medium"
                        >
                          #{tag}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </section>

            {/* Galeria de imagens */}
            {action.images && action.images.length > 1 && (
              <section className="bg-white rounded-2xl shadow-lg border border-gray-100 p-8">
                <h2 className="text-2xl font-bold text-gray-900 mb-6 flex items-center gap-3">
                  <Eye className="w-6 h-6 text-purple-600" />
                  Galeria de Imagens
                </h2>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {action.images.slice(1).map((image, index) => (
                    <div
                      key={index}
                      className="relative rounded-xl overflow-hidden group"
                    >
                      <img
                        src={image.url}
                        alt={image.caption}
                        className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
                      />
                      <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                      <div className="absolute bottom-3 left-3 text-white opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                        <p className="text-sm">{image.caption}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </section>
            )}

            {/* Informações adicionais */}
            {action.additionalInfo && (
              <section className="bg-white rounded-2xl shadow-lg border border-gray-100 p-8">
                <h2 className="text-2xl font-bold text-gray-900 mb-6 flex items-center gap-3">
                  <Info className="w-6 h-6 text-orange-600" />
                  Informações Adicionais
                </h2>

                <div className="bg-orange-50 border border-orange-200 rounded-xl p-6">
                  <p className="text-orange-800 leading-relaxed">
                    {action.additionalInfo}
                  </p>
                </div>
              </section>
            )}
          </div>

          {/* Sidebar - Informações e ações */}
          <div className="space-y-6">
            {/* Card de candidatura */}
            <div className="bg-white rounded-2xl shadow-lg border border-gray-100 p-6 sticky top-6">
              <div className="text-center mb-6">
                <div className="w-16 h-16 bg-gradient-to-br from-green-500 to-emerald-500 rounded-2xl flex items-center justify-center mx-auto mb-4">
                  <Heart className="w-8 h-8 text-white" />
                </div>
                <h3 className="text-xl font-bold text-gray-900 mb-2">
                  Interessado?
                </h3>
                <p className="text-gray-600">Junte-se a esta ação social</p>
              </div>

              {/* Status das vagas */}
              <div className="mb-6">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm text-gray-600">
                    Vagas preenchidas
                  </span>
                  <span className="text-sm font-medium text-gray-900">
                    {action.currentVolunteers}/{action.maxVolunteers}
                  </span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div
                    className="bg-gradient-to-r from-green-500 to-emerald-500 h-2 rounded-full transition-all duration-1000"
                    style={{
                      width: `${
                        (action.currentVolunteers / action.maxVolunteers) * 100
                      }%`,
                    }}
                  ></div>
                </div>
                <p
                  className={`text-sm font-medium mt-2 ${volunteerStatus.color}`}
                >
                  {volunteerStatus.text}
                </p>
              </div>

              {/* Botão de candidatura */}
              <button
                onClick={handleApply}
                disabled={
                  isApplying || action.currentVolunteers >= action.maxVolunteers
                }
                className="w-full py-4 text-lg font-semibold bg-gradient-to-r from-green-500 to-emerald-500 text-white rounded-xl hover:from-green-600 hover:to-emerald-600 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none mb-4"
              >
                {isApplying ? (
                  <div className="flex items-center justify-center gap-2">
                    <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                    Enviando...
                  </div>
                ) : action.currentVolunteers >= action.maxVolunteers ? (
                  "Ação Lotada"
                ) : (
                  "Candidatar-se"
                )}
              </button>

              {/* Informações de contato */}
              <div className="space-y-3 text-sm">
                {action.contactInfo?.phone && (
                  <div className="flex items-center gap-2 text-gray-600">
                    <Phone className="w-4 h-4" />
                    {action.contactInfo.phone}
                  </div>
                )}
                {action.contactInfo?.email && (
                  <div className="flex items-center gap-2 text-gray-600">
                    <Mail className="w-4 h-4" />
                    {action.contactInfo.email}
                  </div>
                )}
                {action.contactInfo?.whatsapp && (
                  <div className="flex items-center gap-2 text-gray-600">
                    <MessageCircle className="w-4 h-4" />
                    {action.contactInfo.whatsapp}
                  </div>
                )}
                {!action.contactInfo?.phone && !action.contactInfo?.email && !action.contactInfo?.whatsapp && (
                  <p className="text-gray-500 text-sm italic">Informações de contato não disponíveis</p>
                )}
              </div>
            </div>

            {/* Informações da ação */}
            <div className="bg-white rounded-2xl shadow-lg border border-gray-100 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">
                Informações da Ação
              </h3>

              <div className="space-y-4">
                <div className="flex items-center gap-3">
                  <Calendar className="w-5 h-5 text-blue-600" />
                  <div>
                    <p className="font-medium text-gray-900">
                      {formatDate(action.date)}
                    </p>
                    <p className="text-sm text-gray-600">
                      às {formatTime(action.time)}
                    </p>
                  </div>
                </div>

                <div className="flex items-center gap-3">
                  <ClockIcon className="w-5 h-5 text-green-600" />
                  <div>
                    <p className="font-medium text-gray-900">
                      {action.duration} horas
                    </p>
                    <p className="text-sm text-gray-600">de duração</p>
                  </div>
                </div>

                <div className="flex items-center gap-3">
                  <MapPin className="w-5 h-5 text-red-600" />
                  <div>
                    <p className="font-medium text-gray-900">
                      {action.location?.city || "Cidade não informada"}, {action.location?.state || "Estado não informado"}
                    </p>
                    <p className="text-sm text-gray-600">
                      {action.location?.address || "Endereço não informado"}
                    </p>
                    {action.location?.neighborhood && (
                      <p className="text-sm text-gray-600">
                        {action.location.neighborhood}
                      </p>
                    )}
                  </div>
                </div>

                <div className="flex items-center gap-3">
                  <Users className="w-5 h-5 text-purple-600" />
                  <div>
                    <p className="font-medium text-gray-900">
                      {action.maxVolunteers} voluntários
                    </p>
                    <p className="text-sm text-gray-600">máximo</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Informações da ONG */}
            <div className="bg-white rounded-2xl shadow-lg border border-gray-100 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">
                Sobre a ONG
              </h3>

              <div className="text-center mb-4">
                {action.ong?.logo ? (
                  <img
                    src={action.ong.logo}
                    alt={action.ong.name || "Logo da ONG"}
                    className="w-16 h-16 rounded-full mx-auto mb-3"
                  />
                ) : (
                  <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-purple-500 rounded-full flex items-center justify-center mx-auto mb-3">
                    <Building2 className="w-8 h-8 text-white" />
                  </div>
                )}

                <h4 className="font-semibold text-gray-900 mb-1">
                  {action.ong?.name || "ONG não informada"}
                </h4>
                <p className="text-sm text-gray-600 mb-2">{action.ong?.area || "Área não informada"}</p>

                {action.ong?.rating && (
                  <div className="flex items-center justify-center gap-1 mb-2">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <Star
                        key={star}
                        className={`w-4 h-4 ${
                          star <= Math.floor(action.ong.rating)
                            ? "text-yellow-400 fill-current"
                            : "text-gray-300"
                        }`}
                      />
                    ))}
                    <span className="text-sm text-gray-600 ml-1">
                      ({action.ong.rating})
                    </span>
                  </div>
                )}

                {action.ong?.totalActions && (
                  <p className="text-xs text-gray-500">
                    {action.ong.totalActions} ações realizadas
                  </p>
                )}
              </div>

              <div className="space-y-3 text-sm">
                <div className="flex items-center gap-2 text-gray-600">
                  <Phone className="w-4 h-4" />
                  {action.ong.contactInfo.phone}
                </div>
                <div className="flex items-center gap-2 text-gray-600">
                  <Mail className="w-4 h-4" />
                  {action.ong.contactInfo.email}
                </div>
                {action.ong.contactInfo.website && (
                  <div className="flex items-center gap-2 text-gray-600">
                    <Globe className="w-4 h-4" />
                    <a
                      href={action.ong.contactInfo.website}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-600 hover:underline"
                    >
                      Visitar site
                    </a>
                  </div>
                )}
              </div>

              <Link
                to={`/ong/${action.ong._id}`}
                className="block w-full mt-4 py-2 text-center text-blue-600 hover:text-blue-700 font-medium border border-blue-200 hover:border-blue-300 rounded-lg transition-colors"
              >
                Ver perfil completo
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ActionDetails;
