import React, { useState, useEffect } from "react";
import { Heart, Users, Target, Award, Clock, MapPin } from "lucide-react";
import { statsService } from "../../services/api";

const StatsSection = () => {
  const [statsData, setStatsData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadStats();
  }, []);

  const loadStats = async () => {
    try {
      const data = await statsService.getGeneral();
      setStatsData(data);
    } catch (err) {
      console.error("Erro ao carregar estatísticas:", err);
      // Em caso de erro, usar dados padrão
      setStatsData(null);
    } finally {
      setLoading(false);
    }
  };

  const formatNumber = (num) => {
    if (num >= 1000000) {
      return (num / 1000000).toFixed(1) + "M+";
    } else if (num >= 1000) {
      return (num / 1000).toFixed(1) + "K+";
    }
    return num.toString();
  };

  const formatHours = (hours) => {
    if (hours >= 1000000) {
      return (hours / 1000000).toFixed(1) + "M+";
    } else if (hours >= 1000) {
      return (hours / 1000).toFixed(1) + "K+";
    }
    return hours.toString();
  };

  // Dados padrão em caso de erro ou carregamento
  const defaultStats = [
    {
      icon: Heart,
      value: "10.000+",
      label: "Voluntários Ativos",
      description: "Pessoas dedicadas a fazer a diferença",
      color: "text-primary-600",
      bgColor: "bg-primary-50",
    },
    {
      icon: Users,
      value: "500+",
      label: "Instituições Parceiras",
      description: "ONGs e organizações confiáveis",
      color: "text-secondary-600",
      bgColor: "bg-secondary-50",
    },
    {
      icon: Target,
      value: "2.000+",
      label: "Ações Realizadas",
      description: "Projetos que transformaram vidas",
      color: "text-success-600",
      bgColor: "bg-success-50",
    },
    {
      icon: Clock,
      value: "50.000+",
      label: "Horas Doadas",
      description: "Tempo dedicado ao voluntariado",
      color: "text-warning-600",
      bgColor: "bg-warning-50",
    },
    {
      icon: MapPin,
      value: "27",
      label: "Estados Cobertos",
      description: "Presença em todo o Brasil",
      color: "text-error-600",
      bgColor: "bg-error-50",
    },
    {
      icon: Award,
      value: "98%",
      label: "Satisfação",
      description: "Voluntários recomendam a plataforma",
      color: "text-purple-600",
      bgColor: "bg-purple-50",
    },
  ];

  // Usar dados dinâmicos se disponíveis, senão usar dados padrão
  const stats = statsData
    ? [
        {
          icon: Heart,
          value: formatNumber(statsData.overview.totalVolunteers),
          label: "Voluntários Ativos",
          description: "Pessoas dedicadas a fazer a diferença",
          color: "text-primary-600",
          bgColor: "bg-primary-50",
        },
        {
          icon: Users,
          value: formatNumber(statsData.overview.totalONGs),
          label: "Instituições Parceiras",
          description: "ONGs e organizações confiáveis",
          color: "text-secondary-600",
          bgColor: "bg-secondary-50",
        },
        {
          icon: Target,
          value: formatNumber(statsData.overview.totalActions),
          label: "Ações Realizadas",
          description: "Projetos que transformaram vidas",
          color: "text-success-600",
          bgColor: "bg-success-50",
        },
        {
          icon: Clock,
          value: formatHours(statsData.overview.estimatedVolunteerHours),
          label: "Horas Doadas",
          description: "Tempo dedicado ao voluntariado",
          color: "text-warning-600",
          bgColor: "bg-warning-50",
        },
        {
          icon: MapPin,
          value: statsData.overview.uniqueCitiesCount + "+",
          label: "Cidades Atendidas",
          description: "Presença em todo o Brasil",
          color: "text-error-600",
          bgColor: "bg-error-50",
        },
        {
          icon: Award,
          value: statsData.overview.completionRate + "%",
          label: "Taxa de Conclusão",
          description: "Ações concluídas com sucesso",
          color: "text-purple-600",
          bgColor: "bg-purple-50",
        },
      ]
    : defaultStats;

  if (loading) {
    return (
      <section className="py-16 bg-white">
        <div className="container-custom">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Carregando estatísticas...</p>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section className="py-20 bg-white relative overflow-hidden">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-16">
          <div className="inline-flex items-center gap-2 bg-primary-50 border border-primary-200 rounded-full px-4 py-2 mb-6">
            <span className="w-2 h-2 bg-primary-500 rounded-full animate-pulse"></span>
            <span className="text-sm font-medium text-primary-700">
              Nossos Números
            </span>
          </div>
          <h2 className="text-3xl md:text-4xl font-heading font-bold text-gray-900 mb-4">
            Números que{" "}
            <span className="text-transparent bg-clip-text bg-gradient-to-r from-primary-600 to-secondary-600">
              Inspiram
            </span>
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            Nossa plataforma já conectou milhares de pessoas a causas que fazem
            a diferença. Veja o impacto real que estamos gerando juntos.
          </p>
        </div>

        {/* Grid de Estatísticas */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {stats.map((stat, index) => (
            <div
              key={index}
              className="group text-center p-8 rounded-2xl bg-white shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2 border border-gray-100"
            >
              {/* Ícone */}
              <div
                className={`w-16 h-16 ${stat.bgColor} rounded-2xl flex items-center justify-center mx-auto mb-6 group-hover:scale-110 transition-transform duration-300`}
              >
                <stat.icon className={`w-8 h-8 ${stat.color}`} />
              </div>

              {/* Valor */}
              <div
                className={`text-4xl font-heading font-bold ${stat.color} mb-3`}
              >
                {stat.value}
              </div>

              {/* Label */}
              <h3 className="text-xl font-heading font-semibold text-gray-900 mb-3">
                {stat.label}
              </h3>

              {/* Descrição */}
              <p className="text-gray-600 leading-relaxed">
                {stat.description}
              </p>
            </div>
          ))}
        </div>

        {/* CTA Section */}
        <div className="mt-16 text-center">
          <div className="bg-gradient-to-r from-primary-600 to-secondary-600 rounded-2xl p-8 md:p-12 text-white relative overflow-hidden">
            {/* Background Pattern */}
            <div className="absolute inset-0 opacity-10">
              <div
                className="absolute inset-0"
                style={{
