import React from "react";
import { Link, useLocation } from "react-router-dom";
import { useAuthStore } from "../../stores/authStore";
import { Home, User, FileText, Award, LogOut } from "lucide-react";

const VolunteerLayout = ({ children }) => {
  const { user, logout } = useAuthStore();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    window.location.href = "/";
  };

  const menuItems = [
    {
      name: "Dashboard",
      path: "/voluntario",
      icon: Home,
      active: location.pathname === "/voluntario",
    },
    {
      name: "Perfil",
      path: "/volunteer/profile",
      icon: User,
      active: location.pathname === "/volunteer/profile",
    },
    {
      name: "Candidaturas",
      path: "/volunteer/applications",
      icon: FileText,
      active: location.pathname === "/volunteer/applications",
    },
    {
      name: "Certificados",
      path: "/volunteer/certificates",
      icon: Award,
      active: location.pathname === "/volunteer/certificates",
    },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-blue-50 to-indigo-50 flex">
      {/* Sidebar */}
      <div className="w-72 bg-white/80 backdrop-blur-xl shadow-2xl border-r border-white/20 flex flex-col">
        {/* Header */}
        <div className="p-8 border-b border-white/20">
          <div className="flex items-center space-x-4">
            <div className="w-16 h-16 bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 rounded-3xl flex items-center justify-center shadow-lg">
              <User className="w-8 h-8 text-white" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-gray-900 mb-1">
                {user?.name || "Voluntário"}
              </h2>
              <p className="text-sm text-gray-500 font-medium">
                Área do Voluntário
              </p>
            </div>
          </div>
        </div>

        {/* Navigation */}
        <nav className="flex-1 p-6">
          <ul className="space-y-3">
            {menuItems.map((item) => {
              const Icon = item.icon;
              return (
                <li key={item.path}>
                  <Link
                    to={item.path}
                    className={`group flex items-center space-x-4 px-6 py-4 rounded-2xl transition-all duration-300 ${
                      item.active
                        ? "bg-gradient-to-r from-blue-500 via-purple-500 to-indigo-600 text-white shadow-xl transform scale-105"
                        : "text-gray-600 hover:bg-white/60 hover:text-gray-900 hover:shadow-lg hover:transform hover:scale-105"
                    }`}
                  >
                    <div
                      className={`w-10 h-10 rounded-xl flex items-center justify-center transition-all duration-300 ${
                        item.active
                          ? "bg-white/20"
                          : "bg-gray-100 group-hover:bg-gradient-to-r group-hover:from-blue-100 group-hover:to-purple-100"
                      }`}
                    >
                      <Icon
                        className={`w-5 h-5 transition-all duration-300 ${
                          item.active
                            ? "text-white"
                            : "text-gray-600 group-hover:text-blue-600"
                        }`}
                      />
                    </div>
                    <span className="font-semibold text-lg">{item.name}</span>
                    {item.active && (
                      <div className="ml-auto w-2 h-2 bg-white rounded-full animate-pulse"></div>
                    )}
                  </Link>
                </li>
              );
            })}
          </ul>
        </nav>

        {/* Footer */}
        <div className="p-6 border-t border-white/20">
          <button
            onClick={handleLogout}
            className="group w-full flex items-center space-x-4 px-6 py-4 text-gray-600 hover:bg-gradient-to-r hover:from-red-50 hover:to-pink-50 hover:text-red-600 rounded-2xl transition-all duration-300 hover:shadow-lg hover:transform hover:scale-105"
          >
            <div className="w-10 h-10 bg-gray-100 group-hover:bg-gradient-to-r group-hover:from-red-100 group-hover:to-pink-100 rounded-xl flex items-center justify-center transition-all duration-300">
              <LogOut className="w-5 h-5 group-hover:text-red-600 transition-all duration-300" />
            </div>
            <span className="font-semibold text-lg">Sair</span>
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Top Bar */}
        <div className="bg-white/80 backdrop-blur-xl shadow-lg border-b border-white/20 px-8 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent">
                {menuItems.find((item) => item.active)?.name || "Dashboard"}
              </h1>
              <p className="text-gray-500 mt-1">
                Gerencie suas atividades voluntárias
              </p>
            </div>
            <div className="flex items-center space-x-4">
              <div className="text-right">
                <p className="text-sm text-gray-500">Bem-vindo,</p>
                <p className="font-semibold text-gray-900">
                  {user?.name || "Voluntário"}
                </p>
              </div>
              <div className="w-12 h-12 bg-gradient-to-br from-blue-500 via-purple-500 to-indigo-600 rounded-2xl flex items-center justify-center shadow-lg">
                <User className="w-6 h-6 text-white" />
              </div>
            </div>
          </div>
        </div>

        {/* Page Content */}
        <div className="flex-1 overflow-auto">{children}</div>
      </div>
    </div>
  );
};

export default VolunteerLayout;
