import React, { useState, useEffect } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import {
  Menu,
  X,
  User,
  Building2,
  LogOut,
  ChevronDown,
  Bell,
} from "lucide-react";
import { useAuthStore } from "../../stores/authStore";
import NotificationBell from "../common/NotificationBell";
import toast from "react-hot-toast";

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  const { isAuthenticated, userType, user, ong, logout } = useAuthStore();

  // Fechar menu ao mudar de rota
  useEffect(() => {
    setIsMenuOpen(false);
    setIsDropdownOpen(false);
  }, [location]);

  // Header scroll effect
  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const handleLogout = () => {
    logout();
    navigate("/");
    toast.success("Logout realizado com sucesso!");
  };

  const navigation = [
    { name: "QUEM SOMOS", href: "/quem-somos" },
    { name: "ONGS", href: "/ongs" },
    { name: "CONTEÚDOS", href: "/conteudos" },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <header className={`header-modern ${scrolled ? "scrolled" : ""}`}>
      <div className="container-custom">
        <div className="d-flex align-items-center justify-content-between py-3">
          {/* Logo Moderno */}
          <Link
            to="/"
            className="d-flex align-items-center text-decoration-none"
          >
            <div className="position-relative me-3">
              <div
                className="bg-gradient-primary rounded-lg d-flex align-items-center justify-content-center"
                style={{ width: "40px", height: "40px" }}
              >
                <span className="text-white fw-bold fs-5">V</span>
              </div>
            </div>
            <div>
              <span className="fw-bold fs-5 text-gray-900 d-block">
                Plataforma de
              </span>
              <span className="fw-bold fs-5 text-primary d-block">
                Voluntariado
              </span>
            </div>
          </Link>

          {/* Navegação Desktop */}
          <nav className="d-none d-lg-flex align-items-center">
            {navigation.map((item) => (
              <Link
                key={item.name}
                to={item.href}
                className={`text-decoration-none mx-3 px-3 py-2 rounded-md fw-medium ${
                  isActive(item.href)
                    ? "text-primary bg-gray-50"
                    : "text-gray-700 hover-text-primary"
                }`}
                style={{ transition: "all 0.2s ease" }}
              >
                {item.name}
              </Link>
            ))}
          </nav>

          {/* Menu de autenticação */}
          <div className="d-flex align-items-center">
            {/* Sino de notificações */}
            {isAuthenticated && (
              <div className="me-3">
                <NotificationBell />
              </div>
            )}

            {isAuthenticated ? (
              <div className="position-relative">
                <button
                  onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                  className="btn btn-link d-flex align-items-center text-decoration-none p-2 rounded-md"
                  style={{ color: "var(--gray-700)" }}
                >
                  <div
                    className="rounded-circle d-flex align-items-center justify-content-center me-2"
                    style={{
                      width: "36px",
                      height: "36px",
                      background: "var(--gradient-primary)",
                      color: "white",
                    }}
                  >
                    {userType === "volunteer" ? (
                      <User size={18} />
                    ) : (
                      <Building2 size={18} />
                    )}
                  </div>
                  <span className="d-none d-md-inline fw-medium">
                    {userType === "volunteer" ? user?.name : ong?.name}
                  </span>
                  <ChevronDown size={16} className="ms-2" />
                </button>

                {/* Dropdown Moderno */}
                {isDropdownOpen && (
                  <div
                    className="position-absolute bg-white rounded-xl shadow-xl border py-2"
                    style={{
                      right: "0",
                      top: "100%",
                      marginTop: "8px",
                      width: "220px",
                      zIndex: 50,
                    }}
                  >
                    <div className="px-3 py-2 border-bottom">
                      <div className="fw-medium text-gray-900">
                        {userType === "volunteer" ? user?.name : ong?.name}
                      </div>
                      <div className="small text-gray-600">
                        {userType === "volunteer"
                          ? "Voluntário"
                          : "Organização"}
                      </div>
                    </div>

                    <Link
                      to={userType === "volunteer" ? "/voluntario" : "/ong"}
                      className="d-block text-decoration-none px-3 py-2 text-gray-700 hover-bg-gray-50"
                      style={{ transition: "all 0.2s ease" }}
                    >
                      <User size={16} className="me-2" />
                      Minha Área
                    </Link>

                    <Link
                      to={
                        userType === "volunteer"
                          ? "/voluntario/perfil"
                          : "/ong/perfil"
                      }
                      className="d-block text-decoration-none px-3 py-2 text-gray-700 hover-bg-gray-50"
                      style={{ transition: "all 0.2s ease" }}
                    >
                      <User size={16} className="me-2" />
                      Perfil
                    </Link>

                    <button
                      onClick={handleLogout}
                      className="btn btn-link w-100 text-start px-3 py-2 text-error d-flex align-items-center"
                      style={{
                        transition: "all 0.2s ease",
                        textDecoration: "none",
                      }}
                    >
                      <LogOut size={16} className="me-2" />
                      <span>Sair</span>
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <button
                onClick={() => {
                  window.dispatchEvent(new CustomEvent("openLoginModal"));
                }}
                className="btn-modern btn-primary"
              >
                ENTRAR
              </button>
            )}

            {/* Menu mobile */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="d-lg-none btn btn-link p-2 ms-3"
              style={{ color: "var(--gray-700)" }}
            >
              {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
            </button>
          </div>
        </div>

        {/* Menu mobile */}
        {isMenuOpen && (
          <div className="d-lg-none py-3 border-top">
            <nav className="d-flex flex-column">
              {navigation.map((item) => (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`text-decoration-none py-2 px-3 rounded-md fw-medium mb-1 ${
                    isActive(item.href)
                      ? "text-primary bg-gray-50"
                      : "text-gray-700"
                  }`}
                  style={{ transition: "all 0.2s ease" }}
                >
                  {item.name}
                </Link>
              ))}

              {isAuthenticated && (
                <>
                  <div className="border-top my-3"></div>
                  <Link
                    to={userType === "volunteer" ? "/voluntario" : "/ong"}
                    className="text-decoration-none py-2 px-3 rounded-md text-gray-700 d-flex align-items-center mb-1"
                    style={{ transition: "all 0.2s ease" }}
                  >
                    <User size={16} className="me-2" />
                    Minha Área
                  </Link>
                  <Link
                    to={
                      userType === "volunteer"
                        ? "/voluntario/perfil"
                        : "/ong/perfil"
                    }
                    className="text-decoration-none py-2 px-3 rounded-md text-gray-700 d-flex align-items-center mb-1"
                    style={{ transition: "all 0.2s ease" }}
                  >
                    <User size={16} className="me-2" />
                    Perfil
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="btn btn-link text-start py-2 px-3 text-error d-flex align-items-center"
                    style={{
                      transition: "all 0.2s ease",
                      textDecoration: "none",
                    }}
                  >
                    <LogOut size={16} className="me-2" />
                    <span>Sair</span>
                  </button>
                </>
              )}
            </nav>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
