import React, { useState, useEffect } from "react";
import {
  Search,
  Filter,
  MapPin,
  Calendar,
  Users,
  Building2,
} from "lucide-react";
import { Link } from "react-router-dom";

const ActionsList = () => {
  const [actions, setActions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [filters, setFilters] = useState({
    area: "",
    city: "",
    state: "",
    search: "",
    dateFrom: "",
    dateTo: "",
  });
  const [showFilters, setShowFilters] = useState(false);

  const areas = [
    "Assistência Social",
    "Educação",
    "Saúde",
    "Meio Ambiente",
    "Cultura",
    "Esporte",
    "Direitos Humanos",
    "Tecnologia",
    "Outros",
  ];

  const states = [
    "AC",
    "AL",
    "AP",
    "AM",
    "BA",
    "CE",
    "DF",
    "ES",
    "GO",
    "MA",
    "MT",
    "MS",
    "MG",
    "PA",
    "PB",
    "PR",
    "PE",
    "PI",
    "RJ",
    "RN",
    "RS",
    "RO",
    "RR",
    "SC",
    "SP",
    "SE",
    "TO",
  ];

  useEffect(() => {
    loadActions();
  }, [filters]);

  const loadActions = async () => {
    try {
      setLoading(true);

      // Dados mockados para teste
      const mockActions = [
        {
          _id: "1",
          title: "Ação Social na Comunidade",
          description:
            "Apoio a famílias carentes com doação de alimentos e roupas. Venha fazer a diferença na vida de quem mais precisa.",
          area: "Assistência Social",
          location: { city: "São Paulo", state: "SP" },
          date: "2024-12-15T10:00:00.000Z",
          time: "09:00",
          duration: 4,
          maxVolunteers: 20,
          currentVolunteers: 8,
          requirements: "Disposição para ajudar e empatia",
          skills: ["comunicação", "organização"],
          tags: ["família", "alimentos", "comunidade"],
          ong: {
            name: "ONG Solidariedade",
            logo: null,
            area: "Assistência Social",
          },
        },
        {
          _id: "2",
          title: "Educação para Todos",
          description:
            "Aulas de reforço escolar para crianças de baixa renda. Ajude a transformar vidas através da educação.",
          area: "Educação",
          location: { city: "Rio de Janeiro", state: "RJ" },
          date: "2024-12-20T14:00:00.000Z",
          time: "14:00",
          duration: 3,
          maxVolunteers: 15,
          currentVolunteers: 12,
          requirements: "Paciência e conhecimento básico",
          skills: ["ensino", "paciência"],
          tags: ["educação", "crianças", "escola"],
          ong: { name: "Instituto Educação", logo: null, area: "Educação" },
        },
        {
          _id: "3",
          title: "Preservação Ambiental",
          description:
            "Limpeza de praias e conscientização sobre poluição marinha. Proteja nosso planeta para as futuras gerações.",
          area: "Meio Ambiente",
          location: { city: "Florianópolis", state: "SC" },
          date: "2024-12-25T08:00:00.000Z",
          time: "08:00",
          duration: 5,
          maxVolunteers: 30,
          currentVolunteers: 25,
          requirements: "Roupa confortável e protetor solar",
          skills: ["conscientização", "trabalho em equipe"],
          tags: ["meio ambiente", "praia", "sustentabilidade"],
          ong: { name: "Eco Brasil", logo: null, area: "Meio Ambiente" },
        },
        {
          _id: "4",
          title: "Apoio a Idosos",
          description:
            "Visitas e atividades recreativas para idosos em asilos. Leve alegria e companhia para quem mais precisa.",
          area: "Assistência Social",
          location: { city: "Belo Horizonte", state: "MG" },
          date: "2024-12-18T14:00:00.000Z",
          time: "14:00",
          duration: 3,
          maxVolunteers: 12,
          currentVolunteers: 7,
          requirements: "Paciência e carinho com idosos",
          skills: ["empatia", "comunicação"],
          tags: ["idosos", "companhia", "carinho"],
          ong: {
            name: "Casa do Idoso",
            logo: null,
            area: "Assistência Social",
          },
        },
        {
          _id: "5",
          title: "Oficina de Artesanato",
          description:
            "Ensine técnicas de artesanato para mulheres em situação de vulnerabilidade social.",
          area: "Cultura",
          location: { city: "Recife", state: "PE" },
          date: "2024-12-22T09:00:00.000Z",
          time: "09:00",
          duration: 4,
          maxVolunteers: 15,
          currentVolunteers: 10,
          requirements: "Conhecimento em artesanato",
          skills: ["artesanato", "ensino"],
          tags: ["artesanato", "mulheres", "oficina"],
          ong: { name: "Arte & Vida", logo: null, area: "Cultura" },
        },
        {
          _id: "6",
          title: "Distribuição de Roupas",
          description:
            "Organize e distribua roupas doadas para famílias carentes da comunidade.",
          area: "Assistência Social",
          location: { city: "Porto Alegre", state: "RS" },
          date: "2024-12-28T10:00:00.000Z",
          time: "10:00",
          duration: 6,
          maxVolunteers: 25,
          currentVolunteers: 18,
          requirements: "Organização e disposição",
          skills: ["organização", "trabalho em equipe"],
          tags: ["roupas", "doação", "famílias"],
          ong: {
            name: "Roupas do Bem",
            logo: null,
            area: "Assistência Social",
          },
        },
        {
          _id: "7",
          title: "Aulas de Futebol",
          description:
            "Ensine futebol para crianças e adolescentes de comunidades carentes.",
          area: "Esporte",
          location: { city: "Fortaleza", state: "CE" },
          date: "2024-12-30T16:00:00.000Z",
          time: "16:00",
          duration: 2,
          maxVolunteers: 8,
          currentVolunteers: 5,
          requirements: "Conhecimento em futebol",
          skills: ["esporte", "ensino"],
          tags: ["futebol", "crianças", "esporte"],
          ong: { name: "Esporte Solidário", logo: null, area: "Esporte" },
        },
        {
          _id: "8",
          title: "Plantio de Árvores",
          description:
            "Participe do reflorestamento de áreas degradadas na cidade.",
          area: "Meio Ambiente",
          location: { city: "Curitiba", state: "PR" },
          date: "2025-01-05T08:00:00.000Z",
          time: "08:00",
          duration: 4,
          maxVolunteers: 20,
          currentVolunteers: 12,
          requirements: "Roupa adequada para campo",
          skills: ["meio ambiente", "trabalho físico"],
          tags: ["plantio", "árvores", "reflorestamento"],
          ong: { name: "Verde Vida", logo: null, area: "Meio Ambiente" },
        },
        {
          _id: "9",
          title: "Aulas de Informática",
          description:
            "Ensine informática básica para adultos que nunca tiveram acesso à tecnologia.",
          area: "Educação",
          location: { city: "Goiânia", state: "GO" },
          date: "2025-01-08T19:00:00.000Z",
          time: "19:00",
          duration: 2,
          maxVolunteers: 10,
          currentVolunteers: 6,
          requirements: "Conhecimento em informática",
          skills: ["informática", "paciência"],
          tags: ["informática", "adultos", "tecnologia"],
          ong: { name: "Digital Inclusão", logo: null, area: "Educação" },
        },
        {
          _id: "10",
          title: "Cuidados com Animais",
          description:
            "Ajude no cuidado e alimentação de animais abandonados em abrigos.",
          area: "Assistência Social",
          location: { city: "Salvador", state: "BA" },
          date: "2025-01-12T09:00:00.000Z",
          time: "09:00",
          duration: 3,
          maxVolunteers: 15,
          currentVolunteers: 9,
          requirements: "Amor por animais",
          skills: ["cuidados", "empatia"],
          tags: ["animais", "abrigo", "cuidados"],
          ong: { name: "Patas do Bem", logo: null, area: "Assistência Social" },
        },
      ];

      // Simular delay de API
      await new Promise((resolve) => setTimeout(resolve, 1000));

      setActions(mockActions);
      setError("");
    } catch (err) {
      setError("Erro ao carregar ações");
      console.error("Erro ao carregar ações:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (name, value) => {
    setFilters((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const clearFilters = () => {
    setFilters({
      area: "",
      city: "",
      state: "",
      search: "",
      dateFrom: "",
      dateTo: "",
    });
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString("pt-BR", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
    });
  };

  const formatTime = (timeString) => {
    if (!timeString) return "";
    return timeString.substring(0, 5); // Remove segundos se houver
  };

  const getVolunteersStatus = (action) => {
    const available = action.maxVolunteers - action.currentVolunteers;
    if (available <= 0) return "Vagas esgotadas";
    if (available <= 3)
      return `${available} vaga${available > 1 ? "s" : ""} restante${
        available > 1 ? "s" : ""
      }`;
    return `${available} vagas disponíveis`;
  };

  const getVolunteersStatusColor = (action) => {
    const available = action.maxVolunteers - action.currentVolunteers;
    if (available <= 0) return "text-red-600";
    if (available <= 3) return "text-orange-600";
    return "text-green-600";
  };

  // Filtrar ações baseado nos filtros aplicados
  const filteredActions = actions.filter((action) => {
    // Filtro de busca por texto
    if (filters.search) {
      const searchLower = filters.search.toLowerCase();
      const searchableText = `${action.title} ${
        action.description
      } ${action.tags.join(" ")} ${action.ong.name}`.toLowerCase();
      if (!searchableText.includes(searchLower)) return false;
    }

    // Filtro por área
    if (filters.area && action.area !== filters.area) return false;

    // Filtro por cidade
    if (
      filters.city &&
      action.location.city.toLowerCase() !== filters.city.toLowerCase()
    )
      return false;

    // Filtro por estado
    if (filters.state && action.location.state !== filters.state) return false;

    // Filtro por data
    if (filters.dateFrom) {
      const actionDate = new Date(action.date);
      const filterDate = new Date(filters.dateFrom);
      if (actionDate < filterDate) return false;
    }

    return true;
  });

  if (loading) {
    return (
      <div className="text-center py-16">
        <div className="relative inline-block">
          <div className="w-20 h-20 border-4 border-blue-200 rounded-full animate-spin"></div>
          <div className="absolute top-0 left-0 w-20 h-20 border-4 border-transparent border-t-blue-600 rounded-full animate-spin"></div>
        </div>
        <h3 className="text-xl font-semibold text-gray-700 mt-6 mb-2">
          Carregando ações...
        </h3>
        <p className="text-gray-500">
          Buscando as melhores oportunidades para você
        </p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-16">
        <div className="w-24 h-24 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-6">
          <svg
            className="w-12 h-12 text-red-500"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z"
            />
          </svg>
        </div>
        <h3 className="text-xl font-semibold text-gray-700 mb-2">
          Ops! Algo deu errado
        </h3>
        <p className="text-red-600 mb-6">{error}</p>
        <button
          onClick={loadActions}
          className="bg-gradient-to-r from-red-500 to-red-600 text-white px-6 py-3 rounded-xl font-semibold hover:from-red-600 hover:to-red-700 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl"
        >
          Tentar novamente
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="text-center mb-12">
        <div className="inline-flex items-center gap-2 bg-blue-50 border border-blue-200 rounded-full px-4 py-2 mb-6">
          <span className="w-2 h-2 bg-blue-500 rounded-full animate-pulse"></span>
          <span className="text-sm font-medium text-blue-700">
            Sistema Ativo
          </span>
        </div>

        <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">
          Ações Sociais{" "}
          <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
            Disponíveis
          </span>
        </h1>

        <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
          Encontre oportunidades de voluntariado que fazem a diferença na sua
          comunidade. Cada ação é uma chance de transformar vidas.
        </p>
      </div>

      {/* Filtros e Busca */}
      <div className="bg-white rounded-2xl shadow-lg border border-gray-100 p-8 mb-12">
        <div className="flex flex-col lg:flex-row gap-6">
          {/* Busca */}
          <div className="flex-1">
            <div className="relative group">
              <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5 group-focus-within:text-blue-500 transition-colors" />
              <input
                type="text"
                placeholder="Buscar por título, descrição ou tags..."
                value={filters.search}
                onChange={(e) => handleFilterChange("search", e.target.value)}
                className="w-full pl-12 pr-4 py-4 border-2 border-gray-200 rounded-xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-300 text-lg placeholder-gray-400"
              />
            </div>
          </div>

          {/* Botão Filtros */}
          <button
            onClick={() => setShowFilters(!showFilters)}
            className="group flex items-center gap-3 px-6 py-4 border-2 border-gray-200 rounded-xl hover:border-blue-300 hover:bg-blue-50 transition-all duration-300 font-medium"
          >
            <Filter className="w-5 h-5 group-hover:text-blue-600 transition-colors" />
            Filtros Avançados
            {Object.values(filters).some((f) => f !== "") && (
              <span className="bg-gradient-to-r from-blue-500 to-purple-500 text-white text-xs rounded-full w-6 h-6 flex items-center justify-center font-bold animate-pulse">
                {Object.values(filters).filter((f) => f !== "").length}
              </span>
            )}
          </button>
        </div>

        {/* Filtros Expandidos */}
        {showFilters && (
          <div className="mt-8 pt-8 border-t border-gray-100">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              {/* Área */}
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-3">
                  Área de Atuação
                </label>
                <select
                  value={filters.area}
                  onChange={(e) => handleFilterChange("area", e.target.value)}
                  className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-300"
                >
                  <option value="">Todas as áreas</option>
                  {areas.map((area) => (
                    <option key={area} value={area}>
                      {area}
                    </option>
                  ))}
                </select>
              </div>

              {/* Cidade */}
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-3">
                  Cidade
                </label>
                <input
                  type="text"
                  placeholder="Digite a cidade"
                  value={filters.city}
                  onChange={(e) => handleFilterChange("city", e.target.value)}
                  className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-300 placeholder-gray-400"
                />
              </div>

              {/* Estado */}
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-3">
                  Estado
                </label>
                <select
                  value={filters.state}
                  onChange={(e) => handleFilterChange("state", e.target.value)}
                  className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-300"
                >
                  <option value="">Todos os estados</option>
                  {states.map((state) => (
                    <option key={state} value={state}>
                      {state}
                    </option>
                  ))}
                </select>
              </div>

              {/* Data */}
              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-3">
                  Data
                </label>
                <input
                  type="date"
                  value={filters.dateFrom}
                  onChange={(e) =>
                    handleFilterChange("dateFrom", e.target.value)
                  }
                  className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:ring-4 focus:ring-blue-100 focus:border-blue-500 transition-all duration-300"
                />
              </div>
            </div>

            {/* Botões de ação */}
            <div className="flex justify-end gap-4 mt-8">
              <button
                onClick={clearFilters}
                className="px-6 py-3 text-gray-600 hover:text-gray-800 hover:bg-gray-100 rounded-xl transition-all duration-300 font-medium"
              >
                Limpar filtros
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Lista de Ações */}
      {filteredActions.length === 0 ? (
        <div className="text-center py-20">
          <div className="w-32 h-32 bg-gradient-to-br from-gray-100 to-gray-200 rounded-full flex items-center justify-center mx-auto mb-8">
            <Building2 className="w-16 h-16 text-gray-400" />
          </div>
          <h3 className="text-2xl font-bold text-gray-900 mb-4">
            {Object.values(filters).some((f) => f !== "")
              ? "Nenhuma ação encontrada"
              : "Nenhuma ação disponível"}
          </h3>
          <p className="text-lg text-gray-600 mb-8 max-w-md mx-auto">
            {Object.values(filters).some((f) => f !== "")
              ? "Tente ajustar os filtros de busca ou verifique se há ações em outras áreas"
              : "Não há ações sociais disponíveis no momento. Volte mais tarde!"}
          </p>
          {Object.values(filters).some((f) => f !== "") && (
            <button
              onClick={clearFilters}
              className="bg-gradient-to-r from-blue-500 to-purple-500 text-white px-8 py-3 rounded-xl font-semibold hover:from-blue-600 hover:to-purple-600 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl"
            >
              Limpar filtros
            </button>
          )}
        </div>
      ) : (
        <div className="flex gap-6 overflow-x-auto pb-4 scrollbar-thin scrollbar-thumb-gray-300 scrollbar-track-gray-100">
          {filteredActions.map((action) => (
            <Link
              key={action._id}
              to={`/acao/${action._id}`}
              className="group bg-white rounded-2xl shadow-lg border border-gray-100 hover:shadow-2xl transition-all duration-300 cursor-pointer transform hover:-translate-y-2 overflow-hidden block flex-shrink-0 w-80 h-96 flex flex-col"
            >
              {/* Header da ação */}
              <div className="p-4 border-b border-gray-100">
                <div className="flex items-start justify-between mb-3">
                  <div className="flex-1">
                    <h3 className="text-lg font-bold text-gray-900 mb-2 line-clamp-2 group-hover:text-blue-600 transition-colors">
                      {action.title}
                    </h3>
                    <div className="flex items-center gap-3 text-sm text-gray-600 mb-2">
                      <div className="w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-500 rounded-full flex items-center justify-center">
                        <Building2 className="w-4 h-4 text-white" />
                      </div>
                      <span className="truncate font-medium">
                        {action.ong?.name}
                      </span>
                    </div>
                  </div>
                  <span className="inline-flex items-center px-3 py-1.5 rounded-full text-xs font-bold bg-gradient-to-r from-blue-100 to-purple-100 text-blue-800 border border-blue-200">
                    {action.area}
                  </span>
                </div>

                <p className="text-gray-600 text-sm line-clamp-3 mb-4 leading-relaxed">
                  {action.description}
                </p>

                {/* Tags */}
                {action.tags && action.tags.length > 0 && (
                  <div className="flex flex-wrap gap-2 mb-3">
                    {action.tags.slice(0, 3).map((tag, index) => (
                      <span
                        key={index}
                        className="inline-flex items-center px-3 py-1.5 rounded-full text-xs font-medium bg-gray-50 text-gray-700 border border-gray-200 hover:bg-gray-100 transition-colors"
                      >
                        {tag}
                      </span>
                    ))}
                    {action.tags.length > 3 && (
                      <span className="inline-flex items-center px-3 py-1.5 rounded-full text-xs font-medium bg-gray-50 text-gray-700 border border-gray-200">
                        +{action.tags.length - 3}
                      </span>
                    )}
                  </div>
                )}
              </div>

              {/* Informações da ação */}
              <div className="p-4">
                <div className="space-y-2 mb-3">
                  {/* Localização */}
                  <div className="flex items-center gap-3 text-sm text-gray-600">
                    <div className="w-6 h-6 bg-blue-100 rounded-full flex items-center justify-center">
                      <MapPin className="w-3 h-3 text-blue-600" />
                    </div>
                    <span className="font-medium">
                      {action.location.city}, {action.location.state}
                    </span>
                  </div>

                  {/* Data e Hora */}
                  <div className="flex items-center gap-3 text-sm text-gray-600">
                    <div className="w-6 h-6 bg-green-100 rounded-full flex items-center justify-center">
                      <Calendar className="w-3 h-3 text-green-600" />
                    </div>
                    <span className="font-medium">
                      {formatDate(action.date)}
                      {action.time && ` às ${formatTime(action.time)}`}
                      {action.duration && ` (${action.duration}h)`}
                    </span>
                  </div>

                  {/* Voluntários */}
                  <div className="flex items-center gap-3 text-sm">
                    <div className="w-6 h-6 bg-purple-100 rounded-full flex items-center justify-center">
                      <Users className="w-3 h-3 text-purple-600" />
                    </div>
                    <span
                      className={`font-medium ${getVolunteersStatusColor(
                        action
                      )}`}
                    >
                      {getVolunteersStatus(action)}
                    </span>
                  </div>
                </div>

                {/* Botão de ação */}
                <div className="mt-3">
                  <div className="w-full bg-gradient-to-r from-blue-500 to-purple-500 text-white py-2.5 px-4 rounded-lg font-medium text-center transform group-hover:scale-105 transition-all duration-300 shadow-lg group-hover:shadow-xl text-sm">
                    Ver detalhes
                  </div>
                </div>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default ActionsList;
