import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Bell,
  Check,
  Archive,
  Trash2,
  Settings,
  Search,
  X,
  AlertCircle,
} from "lucide-react";
import { useNotifications } from "../contexts/NotificationContext";
import { formatDistanceToNow } from "date-fns";
import { ptBR } from "date-fns/locale";

const Notifications = () => {
  const navigate = useNavigate();
  const {
    notifications,
    loading,
    stats,
    markAsRead,
    markMultipleAsRead,
    archiveNotification,
    deleteNotification,
    clearOldNotifications,
    createTestNotification,
  } = useNotifications();

  const [filter, setFilter] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedNotifications, setSelectedNotifications] = useState([]);
  const [showActions, setShowActions] = useState(false);
  const [viewMode, setViewMode] = useState("list"); // list, grid
  const [sortBy, setSortBy] = useState("date"); // date, priority, type

  // Filtrar e pesquisar notificaÃ§Ãµes
  const filteredNotifications = notifications
    .filter((notification) => {
      // Filtro por status
      if (filter !== "all" && notification.status !== filter) {
        return false;
      }

      // Filtro por pesquisa
      if (searchTerm) {
        const searchLower = searchTerm.toLowerCase();
        return (
          notification.title.toLowerCase().includes(searchLower) ||
          notification.message.toLowerCase().includes(searchLower) ||
          notification.type.toLowerCase().includes(searchLower)
        );
      }

      return true;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case "priority":
          const priorityOrder = { urgent: 4, high: 3, normal: 2, low: 1 };
          return priorityOrder[b.priority] - priorityOrder[a.priority];
        case "type":
          return a.type.localeCompare(b.type);
        case "date":
        default:
          return new Date(b.createdAt) - new Date(a.createdAt);
      }
    });

  // Selecionar/deselecionar notificaÃ§Ã£o
  const toggleNotificationSelection = (notificationId) => {
    setSelectedNotifications((prev) =>
      prev.includes(notificationId)
        ? prev.filter((id) => id !== notificationId)
        : [...prev, notificationId]
    );
  };

  // Selecionar todas as notificaÃ§Ãµes visÃ­veis
  const selectAllVisible = () => {
    const visibleIds = filteredNotifications.map((n) => n._id);
    setSelectedNotifications(visibleIds);
  };

  // Deselecionar todas
  const deselectAll = () => {
    setSelectedNotifications([]);
  };

  // Marcar selecionadas como lidas
  const markSelectedAsRead = async () => {
    if (selectedNotifications.length === 0) return;

    await markMultipleAsRead(selectedNotifications);
    setSelectedNotifications([]);
    setShowActions(false);
  };

  // Arquivar selecionadas
  const archiveSelected = async () => {
    if (selectedNotifications.length === 0) return;

    for (const id of selectedNotifications) {
      await archiveNotification(id);
    }
    setSelectedNotifications([]);
    setShowActions(false);
  };

  // Deletar selecionadas
  const deleteSelected = async () => {
    if (selectedNotifications.length === 0) return;

    if (
